```go
package dashboard

import (
    "fmt"
    "log/slog"
    "strings"
    "sync"
    "time"
)

type StatusBar struct {
    mu sync.RWMutex

    // Existing fields
    SessionName string
    AgentCount  int
    Status      string

    // NEW: Handoff status
    HandoffGoal   string
    HandoffNow    string
    HandoffAge    time.Duration
    HandoffPath   string
    ContextUsage  map[string]float64  // paneID -> percentage

    // Cached render
    lastRender string
    dirty      bool
}

func NewStatusBar(sessionName string) *StatusBar {
    return &StatusBar{
        SessionName:  sessionName,
        ContextUsage: make(map[string]float64),
        dirty:        true,
    }
}

func (s *StatusBar) SetHandoff(goal, now string, age time.Duration, path string) {
    s.mu.Lock()
    defer s.mu.Unlock()
    s.HandoffGoal = goal
    s.HandoffNow = now
    s.HandoffAge = age
    s.HandoffPath = path
    s.dirty = true

    slog.Debug("status bar handoff updated",
        "goal_len", len(goal),
        "now_len", len(now),
        "age", age,
    )
}

func (s *StatusBar) SetContextUsage(usage map[string]float64) {
    s.mu.Lock()
    defer s.mu.Unlock()
    s.ContextUsage = usage
    s.dirty = true
}

func (s *StatusBar) Render() string {
    s.mu.RLock()
    defer s.mu.RUnlock()

    if !s.dirty && s.lastRender != "" {
        return s.lastRender
    }

    var b strings.Builder

    // Session info line
    b.WriteString(fmt.Sprintf("Session: %s | Agents: %d | Status: %s",
        s.SessionName, s.AgentCount, s.Status))

    // Handoff context line
    if s.HandoffGoal != "" || s.HandoffNow != "" {
        b.WriteString("\n")
        if s.HandoffGoal != "" {
            b.WriteString(fmt.Sprintf("Goal: %s", truncate(s.HandoffGoal, 60)))
        }
        if s.HandoffNow != "" {
            b.WriteString(fmt.Sprintf(" | Now: %s", truncate(s.HandoffNow, 40)))
        }
        if s.HandoffAge > 0 {
            b.WriteString(fmt.Sprintf(" (%s)", humanizeDuration(s.HandoffAge)))
        }
    }

    // Context usage (if any agent approaching limit)
    if s.hasHighContextUsage() {
        b.WriteString(s.renderContextWarning())
    }

    s.lastRender = b.String()
    s.dirty = false
    return s.lastRender
}

func (s *StatusBar) hasHighContextUsage() bool {
    for _, pct := range s.ContextUsage {
        if pct >= 70 {
            return true
        }
    }
    return false
}

func (s *StatusBar) renderContextWarning() string {
    var warnings []string
    for paneID, pct := range s.ContextUsage {
        if pct >= 85 {
