            warnings = append(warnings, fmt.Sprintf("%s: %.0f%% üü†", paneID, pct))
        }
    }
    if len(warnings) == 0 {
        return ""
    }
    return "\n‚ö†Ô∏è  Context: " + strings.Join(warnings, ", ")
}

func truncate(s string, max int) string {
    if len(s) <= max {
        return s
    }
    return s[:max-3] + "..."
}

func humanizeDuration(d time.Duration) string {
    if d < time.Minute {
        return "just now"
    }
    if d < time.Hour {
        return fmt.Sprintf("%dm ago", int(d.Minutes()))
    }
    if d < 24*time.Hour {
        return fmt.Sprintf("%dh ago", int(d.Hours()))
    }
    return fmt.Sprintf("%dd ago", int(d.Hours()/24))
}
