func (d *Dashboard) refreshHandoffStatus() {
    reader := handoff.NewReader(d.projectDir)

    // Use efficient extraction (cached)
    goal, now, err := reader.ExtractGoalNow(d.sessionName)
    if err != nil {
        slog.Debug("no handoff for session", "session", d.sessionName)
        return
    }

    // Get age from full handoff (less frequent)
    h, path, _ := reader.FindLatest(d.sessionName)
    var age time.Duration
    if h != nil {
        age = time.Since(h.CreatedAt)
    }

    d.statusBar.SetHandoff(goal, now, age, path)

    // Also refresh context usage
    usage := d.contextMonitor.GetAllUsage()
    d.statusBar.SetContextUsage(usage)

    slog.Debug("refreshed handoff status",
        "session", d.sessionName,
        "has_goal", goal != "",
        "context_agents", len(usage),
    )
}

func (d *Dashboard) StartRefreshLoop(interval time.Duration, stop <-chan struct{}) {
    ticker := time.NewTicker(interval)
    defer ticker.Stop()

    // Initial refresh
    d.refreshHandoffStatus()

    for {
        select {
        case <-ticker.C:
            d.refreshHandoffStatus()
        case <-stop:
            return
        }
    }
}
