func getHealthColor(contextPct, providerPct *float64, isRateLimited bool) string {
    if isRateLimited {
        return "red"
    }

    if contextPct != nil {
        switch {
        case *contextPct < 15:
            return "red"
        case *contextPct < 25:
            return "orange"
        case *contextPct < 40:
            return "yellow"
        }
    }

    if providerPct != nil && *providerPct >= 80 {
        return "orange"
    }

    return "green"
}

// Progress bar rendering for TUI
func renderContextBar(pct float64) string {
    // 20-char bar: [‚ñà‚ñà‚ñà‚ñà‚ñà‚ñà‚ñà‚ñà‚ñë‚ñë‚ñë‚ñë‚ñë‚ñë‚ñë‚ñë‚ñë‚ñë‚ñë‚ñë]
    filled := int(pct / 5)
    empty := 20 - filled

    var bar strings.Builder
    bar.WriteString("[")
    for i := 0; i < filled; i++ {
        bar.WriteString("‚ñà")
    }
    for i := 0; i < empty; i++ {
        bar.WriteString("‚ñë")
    }
    bar.WriteString("]")
    return bar.String()
}
