### Go Module Design

```go
package caut

import (
    "context"
    "encoding/json"
    "os/exec"
    "time"
)

// Client interfaces with caut CLI
type Client struct {
    CautPath string        // Path to caut binary (default: "caut")
    Timeout  time.Duration // Command timeout (default: 30s)
}

// NewClient creates a caut client with defaults
func NewClient() *Client {
    return &Client{
        CautPath: "caut",
        Timeout:  30 * time.Second,
    }
}

// IsAvailable checks if caut is installed
func (c *Client) IsAvailable() bool {
    _, err := exec.LookPath(c.CautPath)
    return err == nil
}

// FetchUsage queries caut for provider usage
func (c *Client) FetchUsage(ctx context.Context, providers []string) (*UsageResult, error) {
    args := []string{"usage", "--format", "json"}
    for _, p := range providers {
        args = append(args, "--provider", p)
    }

    ctx, cancel := context.WithTimeout(ctx, c.Timeout)
    defer cancel()

    cmd := exec.CommandContext(ctx, c.CautPath, args...)
    output, err := cmd.Output()
    if err != nil {
        return nil, fmt.Errorf("caut failed: %w", err)
    }

    var result CautResponse
    if err := json.Unmarshal(output, &result); err != nil {
        return nil, fmt.Errorf("parse caut output: %w", err)
    }

    return &UsageResult{
        SchemaVersion: result.SchemaVersion,
        Payloads:      result.Data.Payloads,
        Errors:        result.Errors,
        FetchedAt:     time.Now(),
    }, nil
}

// GetProviderUsage fetches usage for a single provider
func (c *Client) GetProviderUsage(ctx context.Context, provider string) (*ProviderPayload, error) {
    result, err := c.FetchUsage(ctx, []string{provider})
    if err != nil {
        return nil, err
    }
    if len(result.Payloads) == 0 {
        return nil, fmt.Errorf("no data for provider %s", provider)
    }
    return &result.Payloads[0], nil
}

// AgentTypeToProvider maps NTM agent type to caut provider
func AgentTypeToProvider(agentType string) string {
    switch agentType {
    case "cc":
        return "claude"
    case "cod":
        return "codex"
    case "gmi":
        return "gemini"
    default:
        return ""
    }
}
```

