    fmt.Println("Project Allocations:")
    for _, alloc := range plan.Allocations {
        fmt.Printf("  %s (Tier %d, %d beads)\n", 
            alloc.Project.Name, alloc.Project.Tier, alloc.Project.OpenBeads)
        fmt.Printf("    CC: %d, Codex: %d, Gemini: %d\n",
            alloc.CCAgents, alloc.CodAgents, alloc.GmiAgents)
