Combine activity, prompt, and error detection into unified detector.

## Overview
This is the main integration point that combines all detection signals to produce a final AgentStatus.

## Detection Logic

```go
// internal/status/unified.go
package status

import (
    "time"
    "github.com/Dicklesworthstone/ntm/internal/tmux"
)

// UnifiedDetector implements the Detector interface
type UnifiedDetector struct {
    // Configuration
    ActivityThreshold time.Duration // How recent is "active" (default: 5s)
    OutputLines       int           // Lines to capture for analysis (default: 50)
}

func NewDetector() *UnifiedDetector {
    return &UnifiedDetector{
        ActivityThreshold: 5 * time.Second,
        OutputLines:       50,
    }
}

func (d *UnifiedDetector) Detect(paneID string) (AgentStatus, error) {
    status := AgentStatus{
        PaneID:    paneID,
        UpdatedAt: time.Now(),
        State:     StateUnknown,
    }

    // Get pane details
    details, err := tmux.GetPaneDetails(paneID)
    if err != nil {
        return status, err
    }
    status.PaneName = details.Title
    status.AgentType = detectAgentType(details.Title)
    status.LastActive = details.LastActivity

    // Capture recent output
    output, err := tmux.CapturePaneOutput(paneID, d.OutputLines)
    if err != nil {
        return status, err
    }
    status.LastOutput = truncate(output, 200)

    // Detection priority:
    // 1. Check for errors first (most important)
    // 2. Check for idle (at prompt)
    // 3. Check activity recency (working vs unknown)

    // Check for errors
    if errType := DetectErrorInOutput(output); errType != ErrorNone {
        status.State = StateError
        status.ErrorType = errType
        return status, nil
    }

    // Check if at prompt (idle)
    if DetectIdleFromOutput(output, status.AgentType) {
        status.State = StateIdle
        return status, nil
    }

    // Check recent activity
    if time.Since(status.LastActive) < d.ActivityThreshold {
        status.State = StateWorking
        return status, nil
    }

    // Default to unknown if can't determine
    status.State = StateUnknown
    return status, nil
}

func (d *UnifiedDetector) DetectAll(session string) ([]AgentStatus, error) {
    panes, err := tmux.GetPanes(session)
    if err != nil {
        return nil, err
    }

    var statuses []AgentStatus
    for _, pane := range panes {
        s, err := d.Detect(pane.ID)
        if err != nil {
            // Log but continue
            s.State = StateUnknown
        }
        statuses = append(statuses, s)
    }

    return statuses, nil
}

func detectAgentType(paneName string) string {
    // Parse from pane name pattern: session__type_num
    // e.g., "myproject__cc_1" -> "cc"
