// ExecuteSlashCommand runs a slash command in an AI CLI via PTY
func ExecuteSlashCommand(ctx context.Context, cliPath, command string, timeout time.Duration) (string, error) {
    ctx, cancel := context.WithTimeout(ctx, timeout)
